import './App.css';

import React, { useState, useEffect } from 'react';
import NoteList from './components/NoteList';
import NoteForm from './components/NoteForm';
import SearchBar from './components/SearchBar';
import { fetchNotes, createNote, updateNote, deleteNote } from './api';

const App = () => {
  const [notes, setNotes] = useState([]);
  const [currentNote, setCurrentNote] = useState(null);
  const [search, setSearch] = useState('');

  useEffect(() => {
    loadNotes();
  }, [search]);

  const loadNotes = async () => {
    try {
      const { data } = await fetchNotes(`search=${search}`);
      setNotes(data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleAddOrUpdateNote = async (noteData) => {
    try {
      if (currentNote) {
        await updateNote(currentNote._id, noteData);
      } else {
        await createNote(noteData);
      }
      setCurrentNote(null);
      loadNotes();
    } catch (err) {
      console.error(err);
    }
  };

  const handleDeleteNote = async (id) => {
    try {
      await deleteNote(id);
      loadNotes();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-4xl font-bold text-center text-blue-600 mb-6">Notes Manager</h1>
      <SearchBar setSearch={setSearch} />
      <NoteForm
        currentNote={currentNote}
        onAddOrUpdateNote={handleAddOrUpdateNote}
        onCancel={() => setCurrentNote(null)}
      />
      <NoteList
        notes={notes}
        onEdit={(note) => setCurrentNote(note)}
        onDelete={handleDeleteNote}
      />
    </div>
  );
};
export default App;
